package selfProgramming;

public class EXE3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Write a Java program to print the following grid
		
		int[][] grid = new int [5][5];
				
		for (int i = 0; i < 5; i++) 
		{
			for (int j = 0; j < 5; j++) 
			{
				System.out.printf("%2d ", grid[i][j]);
			}
			System.out.println();
	}		

	}

}
